SELECT t1.project_name, t1.customer_name, t1.agent_name, t1.create_user_name, t1.create_time, t1.address_name, t1.project_status, t1.closed_status, t2.total_price, t2.version, t2.install_flag, t2.transport_flag, t2.standard, t2.standard_name, t2.non_standard_apply_status, t2.enquiry_apply_status, t2.non_rate_total_price, t2.average_rate, t2.max_rate, t2.enquiry_audit_user_id, t2.enquiry_audit_user_company_id, t2.enquiry_apply_time, t2.inquiry_price, t2.cost_total_price FROM t_project t1 LEFT JOIN t_project_price t2 on t1.id = t2.project_id
and t1.id =#{projectId,jdbcType=BIGINT}
and t2.id =#{projectPriceId,jdbcType=BIGINT}

SELECT t1.id as projectId, t1.project_name, t1.customer_name, t1.agent_name, t1.create_user_name, t1.create_time, t2.num, t2.unit_price, t2.total_price, t2.elevator_type_id, t2.elevator_type_name, t2.rate, t2.install_price, t2.transport_price, t2.calculate_flag, t3.type FROM t_project t1 INNER JOIN t_project_ele_order t2 on t1.id = t2.project_id INNER JOIN t_elevator_type t3 on t2.elevator_type_id = t3.id
and t1.id =#{projectId,jdbcType=BIGINT}
and t2.id =#{orderId,jdbcType=BIGINT}

SELECT DISTINCT t2.element_name,t2.category_type,t3.info_value,t4.params_unit,t3.element_id,t2.params_key FROM t_project_ele_order t1 INNER JOIN t_elevator_base_element t2 on t1.elevator_type_id = t2.elevator_type_id
and t1.id = #{orderId,jdbcType=BIGINT}
LEFT JOIN t_project_ele_order_base_info t3 on t2.id = t3.element_id LEFT JOIN t_params t4 on t2.params_key = t4.params_name
and t3.order_id = #{orderId,jdbcType=BIGINT}

select

from t_project


        and project_name = #{projectName,jdbcType=VARCHAR}


        and customer_name = #{customerName,jdbcType=VARCHAR}


        and agent_name = #{agentName,jdbcType=VARCHAR}


        and create_user_id = #{createUserId,jdbcType=BIGINT}


        and create_user_name = #{createUserName,jdbcType=VARCHAR}


        and create_time = #{createTime,jdbcType=TIMESTAMP}


        and address_id = #{addressId,jdbcType=VARCHAR}


        and address_name = #{addressName,jdbcType=VARCHAR}


        and project_status = #{projectStatus,jdbcType=INTEGER}


        and company_name = #{companyName,jdbcType=VARCHAR}


        and company_id = #{companyId,jdbcType=BIGINT}


        and closed_status = #{closedStatus,jdbcType=INTEGER}


        and reason_customer_churn_id = #{reasonCustomerChurnId,jdbcType=INTEGER}


        and reason_customer_churn_name = #{reasonCustomerChurnName,jdbcType=VARCHAR}


        and reason_customer_churm_remarks = #{reasonCustomerChurmRemarks,jdbcType=VARCHAR}


        and closed_user_id = #{closedUserId,jdbcType=BIGINT}


        and closed_user_name = #{closedUserName,jdbcType=VARCHAR}


        and closed_time = #{closedTime,jdbcType=TIMESTAMP}


        and closed_project_price_id = #{closedProjectPriceId,jdbcType=BIGINT}

select sum(tt.install_price) installPrice,SUM(tt.transport_price) transportPrice from
(SELECT t.num * t.install_price install_price, t.num * t.transport_price transport_price FROM t_project_ele_order t
and t.version_id=#{projectPriceId,jdbcType=BIGINT}
)tt

SELECT DISTINCT t1.id as orderId,t2.element_name,t2.category_type,t3.info_value,t4.params_unit,t3.element_id,t2.params_key FROM t_project_ele_order t1 INNER JOIN t_elevator_base_element t2 on t1.elevator_type_id = t2.elevator_type_id
and t1.id in
#{item,jdbcType=BIGINT}
LEFT JOIN t_project_ele_order_base_info t3 on t2.id = t3.element_id LEFT JOIN t_params t4 on t2.params_key = t4.params_name
and t3.order_id in
#{item,jdbcType=BIGINT}

SELECT 
FROM t_project


        and project_name = #{projectPageVO.projectName,jdbcType=VARCHAR}


        and customer_name = #{projectPageVO.customerName,jdbcType=VARCHAR}


        and agent_name = #{projectPageVO.agentName,jdbcType=VARCHAR}


        and create_user_id = #{projectPageVO.createUserId,jdbcType=BIGINT}


        and create_user_name = #{projectPageVO.createUserName,jdbcType=VARCHAR}


        and create_time = #{projectPageVO.createTime,jdbcType=TIMESTAMP}


        and address_id = #{projectPageVO.addressId,jdbcType=VARCHAR}


        and address_name = #{projectPageVO.addressName,jdbcType=VARCHAR}


        and project_status = #{projectPageVO.projectStatus,jdbcType=INTEGER}


        and company_name = #{projectPageVO.companyName,jdbcType=VARCHAR}


        and company_id = #{projectPageVO.companyId,jdbcType=BIGINT}


        and closed_status = #{projectPageVO.closedStatus,jdbcType=INTEGER}


        and reason_customer_churn_id = #{projectPageVO.reasonCustomerChurnId,jdbcType=INTEGER}


        and reason_customer_churn_name = #{projectPageVO.reasonCustomerChurnName,jdbcType=VARCHAR}


        and reason_customer_churm_remarks = #{projectPageVO.reasonCustomerChurmRemarks,jdbcType=VARCHAR}


        and closed_user_id = #{projectPageVO.closedUserId,jdbcType=BIGINT},


        and closed_user_name = #{projectPageVO.closedUserName,jdbcType=VARCHAR}


        and closed_time = #{projectPageVO.closedTime,jdbcType=TIMESTAMP}


        and closed_project_price_id = #{projectPageVO.closedProjectPriceId,jdbcType=BIGINT}


        and (project_name like concat("%",#{projectPageVO.search,jdbcType=VARCHAR},"%")
        or customer_name like concat("%",#{projectPageVO.search,jdbcType=VARCHAR},"%")
        or agent_name like concat("%",#{projectPageVO.search,jdbcType=VARCHAR},"%")
        or address_name like concat("%",#{projectPageVO.search,jdbcType=VARCHAR},"%")
        or create_user_name like concat("%",#{projectPageVO.search,jdbcType=VARCHAR},"%")
        )


        order by ${projectPageVO.orderBy}

SELECT
id as projectId,
project_name,
customer_name,
agent_name,
create_user_id,
create_user_name as realName,
create_time,
address_id,
address_name,
project_status,
company_name,
company_id,
closed_status,
reason_customer_churn_id,
reason_customer_churn_name,
reason_customer_churm_remarks,
closed_user_id,
closed_user_name,
closed_time,
closed_project_price_id
FROM t_project


        and project_name LIKE concat(#{projectPageVO.projectName,jdbcType=VARCHAR},"%")


        and customer_name LIKE concat(#{projectPageVO.customerName,jdbcType=VARCHAR},"%")


        and agent_name LIKE concat(#{projectPageVO.agentName,jdbcType=VARCHAR},"%")


        and create_user_id = #{projectPageVO.createUserId,jdbcType=BIGINT}


        and create_user_name LIKE concat(#{projectPageVO.createUserName,jdbcType=VARCHAR},"%")


        and create_time = #{projectPageVO.createTime,jdbcType=TIMESTAMP}


        and address_id = #{projectPageVO.addressId,jdbcType=VARCHAR}


        and address_name = #{projectPageVO.addressName,jdbcType=VARCHAR}


        and project_status = #{projectPageVO.projectStatus,jdbcType=INTEGER}


        and company_name = #{projectPageVO.companyName,jdbcType=VARCHAR}


        and company_id = #{projectPageVO.companyId,jdbcType=BIGINT}


        and closed_status = #{projectPageVO.closedStatus,jdbcType=INTEGER}


        and reason_customer_churn_id = #{projectPageVO.reasonCustomerChurnId,jdbcType=INTEGER}


        and reason_customer_churn_name = #{projectPageVO.reasonCustomerChurnName,jdbcType=VARCHAR}


        and reason_customer_churm_remarks = #{projectPageVO.reasonCustomerChurmRemarks,jdbcType=VARCHAR}


        and closed_user_id = #{projectPageVO.closedUserId,jdbcType=BIGINT},


        and closed_user_name = #{projectPageVO.closedUserName,jdbcType=VARCHAR}


        and closed_time = #{projectPageVO.closedTime,jdbcType=TIMESTAMP}
